namespace PalindromeVerificationAppTests
{
    using PalindromeVerificationApp;
    using Xunit;

    public class PalindromeVerificatorTests
    {
        [Theory]
        [InlineData("abba")]
        [InlineData("abcba")]
        [InlineData("abccccccccccccba")]
        [InlineData("123456654321")]
        [InlineData("123abcba321")]
        [InlineData("abcdefghijklmnopqrstuyzyutsrqponmlkjihgfedcba")]
        [InlineData("")]
        public void GivenStringThatIsPalindrome_ShouldReturnTrue(string @base)
        {
            Assert.True(new PalindromeVerificator().IsPalindrome(@base));
        }

        [Theory]
        [InlineData("abc")]
        [InlineData("abcdefghi")]
        [InlineData("123456789")]
        [InlineData("abcdefghijklmnopqrsutyzyutsrqponmlkjihgfedcba")]
        [InlineData("abcccccccfhcccccccba")]
        [InlineData("abcccccccfihcccccccba")]
        [InlineData("")]
        public void GivenStringThatIsNotPalindrome_ShouldReturnFalse(string @base)
        {
            Assert.False(new PalindromeVerificator().IsPalindrome(@base));

        }
    }
}
